-- Set temp timeout due to large modification
-- Time is 5 minutes in milliseconds
SET statement_timeout = 300000;
SET lock_timeout = 300000;
SET idle_in_transaction_session_timeout = 300000;

-- creating the country table
CREATE TABLE IF NOT EXISTS re_countries
(
id				UUID		NOT NULL,
country			VARCHAR(2)	NOT NULL,
country_name	VARCHAR(50)	NOT NULL,
created_at		TIMESTAMP	NOT NULL DEFAULT NOW(),
updated_at		TIMESTAMP	NOT NULL DEFAULT NOW(),
CONSTRAINT re_countries_pkey PRIMARY KEY (id),
CONSTRAINT unique_re_countries UNIQUE (country)
);

COMMENT ON TABLE re_countries IS 'Stores US country codes and names';
COMMENT ON COLUMN re_countries.country IS 'The unique 2 character country code';
COMMENT ON COLUMN re_countries.country_name IS 'The name of the country';

CREATE INDEX IF NOT EXISTS idx_country ON re_countries (country);

-- populating with data

-- Update the "addresses" table since it contains an existing "country" column
ALTER TABLE addresses ADD COLUMN IF NOT EXISTS country_id uuid;

-- Populate the new column with the 'US' UUID for existing records
UPDATE addresses
SET country_id = (SELECT id FROM re_countries WHERE country = 'US')
WHERE country IS NULL OR country IN ('US', 'United States', 'USA');

-- Drop the old country column
ALTER TABLE addresses DROP COLUMN country;

-- Add the foreign key constraint to ensure referential integrity
ALTER TABLE addresses
ADD CONSTRAINT fk_country_addresses
FOREIGN KEY (country_id) REFERENCES re_countries(id);
