-- Create table to static UB allowances, used for calculating entitlements.ub_allowance for orders
CREATE TABLE IF NOT EXISTS ub_allowances (
    branch text,
    grade text,
    orders_type text,
    dependents_authorized boolean,
    accompanied_tour boolean,
    base_base_ub_weight_allowance integer
);

COMMENT ON TABLE ub_allowances IS 'Base UB allowance values used to lookup and calculate entitlements.ub_allowance for orders';
COMMENT ON COLUMN ub_allowances.branch IS 'Branch of service for service member';
COMMENT ON COLUMN ub_allowances.grade IS 'Grade of service member';
COMMENT ON COLUMN ub_allowances.orders_type IS 'Either PERMANENT_CHANGE_OF_STATION or TEMPORARY_DUTY order types';
COMMENT ON COLUMN ub_allowances.dependents_authorized IS 'Depedents authorized on order';
COMMENT ON COLUMN ub_allowances.accompanied_tour IS 'Is an accompanied tour';
COMMENT ON COLUMN ub_allowances.base_ub_weight_allowance IS 'The base UB weight allowance for given values for branch, grade, orders_type, dependents_authorized, accompanied_tour. Used to calculate the orders.entitlements.ub_allowance value, which can increase from the number in this table depending on number of dependents.';


